package com.matias.features.diimport com.matias.data.cache.featureflag.FeatureFlaghCacheimport com.matias.data.provider.globalconfig.FeatureFlagProviderImplimport com.matias.domain.provider.globalconfig.FeatureFlagProviderimport com.matias.domain.usecases.globalconfig.GetFeatureEnableUseCaseimport dagger.Moduleimport dagger.Provides@Moduleclass MainModule {	@Provides	@MainScope	fun providesFeatureFlagProvider(featureFlaghCache: FeatureFlaghCache): FeatureFlagProvider =		FeatureFlagProviderImpl(featureFlaghCache)	@Provides	@MainScope	fun providesGetFeatureEnableUseCase(featureFlagProvider: FeatureFlagProvider): GetFeatureEnableUseCase =		GetFeatureEnableUseCase(featureFlagProvider)}