package com.matias.features.di.mainimport android.content.Contextimport com.matias.domain.usecases.globalconfig.GetFeatureEnableUseCaseimport com.matias.features.MainItemPageHelperimport com.matias.features.ui.main.activity.MainActivityPresenterimport dagger.Moduleimport dagger.Provides@Moduleclass MainActivityModule {	@Provides	fun provideMainActivityPresenter(getFeatureEnableUseCase: GetFeatureEnableUseCase): MainActivityPresenter =		MainActivityPresenter(getFeatureEnableUseCase)	@Provides	fun provideMainItemPageHelper(context: Context): MainItemPageHelper =		MainItemPageHelper(context)}