package com.matias.features.di.mainimport android.content.Contextimport com.matias.domain.provider.mainposition.MainPositionProviderimport com.matias.domain.usecases.globalconfig.GetFeatureEnableUseCaseimport com.matias.domain.usecases.mainposition.FetchMainPositionUseCaseimport com.matias.features.MainItemPageHelperimport com.matias.features.ui.main.activity.MainActivityimport com.matias.features.ui.main.activity.MainActivityPresenterimport dagger.Moduleimport dagger.Provides/** * Dagger Module * * @see [MainActivity] client * @see [MainActivitySubcomponent] component */@Moduleclass MainActivityModule {	@Provides	fun provideMainActivityPresenter(		getFeatureEnableUseCase: GetFeatureEnableUseCase,		fetchMainPositionUseCase: FetchMainPositionUseCase	): MainActivityPresenter =		MainActivityPresenter(getFeatureEnableUseCase, fetchMainPositionUseCase)	@Provides	fun provideMainItemPageHelper(context: Context): MainItemPageHelper =		MainItemPageHelper(context)	@Provides	fun provideGetMainPositionUseCase(mainPositionProvider: MainPositionProvider): FetchMainPositionUseCase =		FetchMainPositionUseCase(mainPositionProvider)}