package com.matias.features.di.mainimport androidx.appcompat.app.AppCompatActivityimport androidx.fragment.app.FragmentManagerimport com.matias.domain.provider.mainposition.MainPositionProviderimport com.matias.domain.usecases.globalconfig.GetFeatureEnableUseCaseimport com.matias.domain.usecases.mainposition.FetchMainPositionUseCaseimport com.matias.domain.usecases.mainposition.GetNoveltyListUseCaseimport com.matias.domain.usecases.mainposition.GetProductOfferListUseCaseimport com.matias.features.ui.main.activity.MainActivityimport com.matias.features.ui.main.activity.MainActivityPresenterimport com.matias.features.ui.main.fragments.home.novelty.NoveltyPagerAdapterimport dagger.Moduleimport dagger.Provides/** * Dagger Module * * @see [MainActivity] client * @see [MainActivitySubcomponent] component */@Moduleclass MainActivityModule(private val activity: AppCompatActivity) {	@Provides	fun provideFragmentManager(): FragmentManager =		activity.supportFragmentManager	@Provides	fun provideMainActivityPresenter(		getFeatureEnableUseCase: GetFeatureEnableUseCase,		fetchMainPositionUseCase: FetchMainPositionUseCase,		getNoveltyListUseCase: GetNoveltyListUseCase,		getProductOfferListUseCase: GetProductOfferListUseCase	): MainActivityPresenter =		MainActivityPresenter(getFeatureEnableUseCase, fetchMainPositionUseCase, getNoveltyListUseCase, getProductOfferListUseCase)	@Provides	fun provideGetNoveltyListUseCase(		mainPositionProvider: MainPositionProvider	): GetNoveltyListUseCase =		GetNoveltyListUseCase(mainPositionProvider)	@Provides	fun provideGetProductOfferListUseCase(		mainPositionProvider: MainPositionProvider	): GetProductOfferListUseCase =		GetProductOfferListUseCase(mainPositionProvider)	@Provides	fun provideGetMainPositionUseCase(mainPositionProvider: MainPositionProvider): FetchMainPositionUseCase =		FetchMainPositionUseCase(mainPositionProvider)	@Provides	fun provideNoveltyPagerAdapter(fragmentManager: FragmentManager): NoveltyPagerAdapter =		NoveltyPagerAdapter(fragmentManager)}