package com.matias.features.di.main.fragments.homeimport androidx.fragment.app.Fragmentimport androidx.fragment.app.FragmentManagerimport com.matias.domain.provider.mainposition.MainPositionProviderimport com.matias.domain.usecases.mainposition.GetNoveltyListUseCaseimport com.matias.features.ui.main.fragments.home.MainHomeFragmentimport com.matias.features.ui.main.fragments.home.novelty.NoveltyPagerAdapterimport dagger.Moduleimport dagger.Provides/** * Dagger Module * * @see [MainHomeFragment] client * @see [MainHomeFragmentSubcomponent] component */@Moduleclass MainHomeFragmentModule(private var fragment: Fragment) {	@Provides	fun provideFragment(): Fragment = fragment	@Provides	fun provideFragmentManager(fragment: Fragment): FragmentManager =		fragment.childFragmentManager	@Provides	fun provideNoveltyPagerAdapter(childFragmentManager: FragmentManager): NoveltyPagerAdapter =		NoveltyPagerAdapter(childFragmentManager)	@Provides	fun provideGetNoveltyListUseCase(		mainPositionProvider: MainPositionProvider	): GetNoveltyListUseCase =		GetNoveltyListUseCase(mainPositionProvider)}