package com.matias.features.uiimport com.matias.core.base.BaseApplicationimport com.matias.features.di.DaggerMainComponentimport com.matias.features.di.MainComponentimport com.matias.features.di.MainModuleinternal class MainUiComponent {        companion object {        internal val component: MainComponent by lazy(mode = LazyThreadSafetyMode.NONE) {            createComponent()        }                @Suppress("DEPRECATION")        private fun createComponent(): MainComponent =                DaggerMainComponent.builder()                        .baseComponent(BaseApplication.baseComponent)                        .mainModule(MainModule())                        .build()    }    }