package com.matias.features.ui.mainimport android.animation.ObjectAnimatorimport android.graphics.drawable.AnimatedVectorDrawableimport android.os.Bundleimport android.os.Handlerimport android.view.Menuimport android.view.MenuItemimport androidx.core.graphics.drawable.DrawableCompatimport androidx.core.view.GravityCompatimport androidx.drawerlayout.widget.DrawerLayoutimport androidx.vectordrawable.graphics.drawable.AnimatedVectorDrawableCompatimport androidx.vectordrawable.graphics.drawable.ArgbEvaluatorimport com.google.android.material.navigation.NavigationViewimport com.matias.core.base.mvp.BasePresenterActivityimport com.matias.features.Rimport com.matias.features.di.main.MainActivityModuleimport com.matias.features.di.main.MainActivitySubcomponentimport com.matias.features.ui.MainUiComponentimport kotlinx.android.synthetic.main.activity_main.*import kotlinx.android.synthetic.main.app_bar_main.*import kotlinx.android.synthetic.main.content_main.*import kotlinx.android.synthetic.main.toolbar_main.*class MainActivity : BasePresenterActivity<MainActivity, MainActivityPresenter, MainActivitySubcomponent>(),	MainActivityContract.View, NavigationView.OnNavigationItemSelectedListener {	override fun bindComponent(): MainActivitySubcomponent =		MainUiComponent.component.plus(MainActivityModule())	override fun bindLayout(): Int =		R.layout.activity_main	override fun onCreate(savedInstanceState: Bundle?) {		super.onCreate(savedInstanceState)		setContentView(R.layout.activity_main)		navView.setNavigationItemSelectedListener(this)		setupToolbar()		setupPager()	}	private fun setupToolbar() {		setSupportActionBar(toolbar)		toolbar.navigationIcon = null		supportActionBar?.setDisplayHomeAsUpEnabled(false)		buttonNavigation.setOnClickListener {			if (!drawerLayout.isDrawerOpen(GravityCompat.START)) drawerLayout.openDrawer(GravityCompat.START)			else drawerLayout.closeDrawer(GravityCompat.END)		}		buttonSearch.setOnClickListener { onToolbarOptionButtonClick(it.id) }		buttonNotification.setOnClickListener { onToolbarOptionButtonClick(it.id) }		iconFilterSearchButton.setOnClickListener { onToolbarOptionButtonClick(it.id) }		labelFilterSearchButton.setOnClickListener { onToolbarOptionButtonClick(it.id) }//		buttonShopCart.setOnClickListener { onToolbarOptionButtonClick(it.id) }//		buttonOffers.setOnClickListener { onToolbarOptionButtonClick(it.id) }		Handler().postDelayed({			val wrappedDrawableA = DrawableCompat.wrap(buttonNotification.drawable)			buttonNotification.setImageDrawable(wrappedDrawableA)			val dA = buttonNotification.drawable			if (dA is AnimatedVectorDrawableCompat) dA.start()			else if (dA is AnimatedVectorDrawable) dA.start()			val wrappedDrawableB = DrawableCompat.wrap(buttonNavigation.drawable)			buttonNavigation.setImageDrawable(wrappedDrawableB)			val dB = buttonNavigation.drawable			if (dB is AnimatedVectorDrawableCompat) dB.start()			else if (dB is AnimatedVectorDrawable) dB.start()		}, 300)	}	/**	 * Setup the pager that will contain all the main fragments.	 */	private fun setupPager() {		pager.adapter = MainPagerAdapter(supportFragmentManager, 0)	}	override fun onCreateOptionsMenu(menu: Menu): Boolean =		false	override fun onNavigationItemSelected(item: MenuItem): Boolean {		when (item.itemId) {			R.id.nav_home -> {				// Handle the camera action			}		}		val drawerLayout: DrawerLayout = findViewById(R.id.drawerLayout)		drawerLayout.closeDrawer(GravityCompat.START)		return true	}	override fun onBackPressed() {		val drawerLayout: DrawerLayout = findViewById(R.id.drawerLayout)		if (drawerLayout.isDrawerOpen(GravityCompat.START)) {			drawerLayout.closeDrawer(GravityCompat.START)		} else {			super.onBackPressed()		}	}	/*******************************************************************************************************************	 * [MainActivityContract.View] implementation	 */	override fun changeStatusBarColor(toColor: Int) {		ObjectAnimator.ofObject(window, "statusBarColor", ArgbEvaluator(), window.statusBarColor, toColor).apply {			duration = 400			start()		}	}	override fun onToolbarOptionButtonClick(id: Int) {		when (id) {			R.id.buttonSearch -> { showToast("Search") }			R.id.buttonNotification -> { showToast("Notofications") }			R.id.iconFilterSearchButton, R.id.labelFilterSearchButton -> { showToast("Filter search") }		}	}}