package com.matias.features.ui.mainimport android.os.Bundleimport androidx.core.view.GravityCompatimport com.matias.core.base.mvp.BasePresenterActivityimport com.matias.features.Rimport com.matias.features.di.main.MainActivityModuleimport com.matias.features.di.main.MainActivitySubcomponentimport com.matias.features.ui.MainUiComponentimport kotlinx.android.synthetic.main.activity_main.*import kotlinx.android.synthetic.main.app_bar_main.*class MainActivity : BasePresenterActivity<MainActivity, MainActivityPresenter, MainActivitySubcomponent>(),        MainActivityContract.View {            override fun bindComponent(): MainActivitySubcomponent =            MainUiComponent.component.plus(MainActivityModule())        override fun bindLayout(): Int =            R.layout.activity_main        override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_main)        setSupportActionBar(toolbar)            }        override fun onBackPressed() {        if (drawer_layout.isDrawerOpen(GravityCompat.START)) {            drawer_layout.closeDrawer(GravityCompat.START)        } else {            super.onBackPressed()        }    }}