package com.matias.features.ui.main.activityimport android.animation.ObjectAnimatorimport android.graphics.drawable.AnimatedVectorDrawableimport android.os.Bundleimport android.os.Handlerimport android.view.Menuimport android.view.Viewimport androidx.core.graphics.drawable.DrawableCompatimport androidx.core.view.GravityCompatimport androidx.drawerlayout.widget.DrawerLayoutimport androidx.vectordrawable.graphics.drawable.AnimatedVectorDrawableCompatimport androidx.vectordrawable.graphics.drawable.ArgbEvaluatorimport com.matias.components.bottomnavigation.bottomnavmain.StylingBottomNavMainimport com.matias.core.base.mvp.BasePresenterActivityimport com.matias.domain.models.featureflag.Featuresimport com.matias.domain.models.item.ItemMainPageModelimport com.matias.features.MainItemPageHelperimport com.matias.features.Rimport com.matias.features.di.main.MainActivityModuleimport com.matias.features.di.main.MainActivitySubcomponentimport com.matias.features.ui.MainUiComponentimport kotlinx.android.synthetic.main.activity_main.*import kotlinx.android.synthetic.main.app_bar_main.*import kotlinx.android.synthetic.main.content_main.*import kotlinx.android.synthetic.main.toolbar_main.*import javax.inject.Injectclass MainActivity : BasePresenterActivity<MainActivity, MainActivityPresenter, MainActivitySubcomponent>(),	MainActivityContract.View {	@Inject	lateinit var mainItemPageHelper: MainItemPageHelper	private var itemMainPageList: MutableList<ItemMainPageModel>? = null	override fun bindComponent(): MainActivitySubcomponent =		MainUiComponent.component.plus(MainActivityModule())	override fun bindLayout(): Int =		R.layout.activity_main	override fun onCreate(savedInstanceState: Bundle?) {		super.onCreate(savedInstanceState)		setContentView(R.layout.activity_main)		itemMainPageList = mainItemPageHelper.getItemMainPageList(			presenter.getTabEnableList(mainItemPageHelper.tagList)		)		setupToolbar()		setupBottomNavigationView()		setupPager()	}	private fun setupToolbar() {		setSupportActionBar(toolbar)		toolbar.navigationIcon = null		supportActionBar?.setDisplayHomeAsUpEnabled(false)		buttonNavigation.setOnClickListener {			if (!drawerLayout.isDrawerOpen(GravityCompat.START)) drawerLayout.openDrawer(GravityCompat.START)			else drawerLayout.closeDrawer(GravityCompat.END)		}		buttonSearch.setOnClickListener { onToolbarOptionButtonClick(it.id) }		buttonNotification.setOnClickListener { onToolbarOptionButtonClick(it.id) }		iconFilterSearchButton.setOnClickListener { onToolbarOptionButtonClick(it.id) }		labelFilterSearchButton.setOnClickListener { onToolbarOptionButtonClick(it.id) }		// TODO handle propperly.		Handler().postDelayed({			val wrappedDrawableA = DrawableCompat.wrap(buttonNotification.drawable)			buttonNotification.setImageDrawable(wrappedDrawableA)			val dA = buttonNotification.drawable			if (dA is AnimatedVectorDrawableCompat) dA.start()			else if (dA is AnimatedVectorDrawable) dA.start()			val wrappedDrawableB = DrawableCompat.wrap(buttonNavigation.drawable)			buttonNavigation.setImageDrawable(wrappedDrawableB)			val dB = buttonNavigation.drawable			if (dB is AnimatedVectorDrawableCompat) dB.start()			else if (dB is AnimatedVectorDrawable) dB.start()		}, 300)	}	/**	 *	 */	private fun setupBottomNavigationView() {		// TODO Mannage botton nav icon badges.		itemMainPageList?.let {			bottomNav.setup(it, onBottomNavMainItemSelectedListener)		}	}	/**	 * Setup the pager that will contain all the main fragments.	 */	private fun setupPager() {		itemMainPageList?.let { itemList ->			pager.apply {				adapter = MainPagerAdapter(itemList, supportFragmentManager, 0)				setSwipePagingEnabled(false)			}		}	}	override fun onCreateOptionsMenu(menu: Menu): Boolean =		false	override fun onBackPressed() {		val drawerLayout: DrawerLayout = findViewById(R.id.drawerLayout)		if (drawerLayout.isDrawerOpen(GravityCompat.START)) {			drawerLayout.closeDrawer(GravityCompat.START)		} else {			super.onBackPressed()		}	}	/**	 *	 */	private val onBottomNavMainItemSelectedListener: StylingBottomNavMain.SimpleOnBottomNavMainItemSelectedListener =		object : StylingBottomNavMain.SimpleOnBottomNavMainItemSelectedListener() {			override fun onItemSelected(view: View?) {				super.onItemSelected(view)				view?.let { onBottomNavItemSelected(it.tag as Features) }			}		}	/*******************************************************************************************************************	 * [MainActivityContract.View] implementation	 */	override fun onBottomNavItemSelected(tag: Features) {		when (tag) {			Features.TAB_HOME -> pager.setCurrentItem(0, false)			Features.TAB_PRODUCTS -> pager.setCurrentItem(1, false)			Features.TAB_SHOP_CART -> pager.setCurrentItem(2, false)			Features.TAB_CONTACT_US -> pager.setCurrentItem(3, false)			Features.TAB_PROFILE -> pager.setCurrentItem(4, false)			else -> {			}		}	}	override fun changeStatusBarColor(toColor: Int) {		ObjectAnimator.ofObject(window, "statusBarColor", ArgbEvaluator(), window.statusBarColor, toColor).apply {			duration = 400			start()		}	}	override fun onToolbarOptionButtonClick(id: Int) {		when (id) {			R.id.buttonSearch -> {				showToast("Search")			}			R.id.buttonNotification -> {				showToast("Notofications")			}			R.id.iconFilterSearchButton, R.id.labelFilterSearchButton -> {				showToast("Filter search")			}		}	}}